void Put::identify_command(string line, vector<User *> &users, User *&currentUser, int &lessonID_,
                           vector<Lesson *> &lessons, vector<Course *> &courses, vector<Major *> &majors)
{

    istringstream iss(line);
    string command;
    iss >> command;
    size_t i = line.find("?");
    if (i != string::npos)
    {
        line = line.substr(i + 2);
    }

    else
    {
        throw BadRequest();
    }

    istringstream iss2(line);
    if (command == "my_courses")
    {
        string title;
        string lessonID_;
        iss2 >> title;
        iss2 >> lessonID_;

        if (currentUser == nullptr)
        {
            throw Inaccessibility();
        }

        else
        {
            if (title == "id")
            {
                if (!can_convert_to_int(lessonID_))
                {
                    throw BadRequest();
                }

                if (!lesson_exists(stoi(lessonID_), lessons))
                {
                    throw Absence();
                }




                User *chosenUser = find_user_by_id(key, users);
                chosenUser->show_personal_page();
            }

            else
            {
                throw BadRequest();
            }
        }
    }

    else
    {
        throw Absence();
    }
}
